Rule.Source:

import sailpoint.api.SailPointContext;
import sailpoint.object.Identity;

List li = new ArrayList();

List id = context.getObjects(Identity.class);
for(Identity iden: id){
  Identity manager = iden.getManager();
  if(manager != null){
    String mgrname = manager.getName();
    if(!li.contains(mgrname)){
  		li.add(mgrname);}


  }
}
return li;
